// Function to select a random response
std::string get_random_fallback_response() {
    std::srand(std::time(0)); // Initialize the random number generator
    int index = std::rand() % FALLBACK_RESPONSES.size(); // Select a random index
    return FALLBACK_RESPONSES[index]; // Return the corresponding response
}

